"""
Constants used for stemming module
"""
import pyarabic.araby as ar
import re

VERB_LIST=()
def is_verb_stamp(stem):
    """
    Test if the stem can be found as verb stamp
    """
    return verb_stamp(stem) in VERB_LIST
        
def verb_stamp(verb):
    """
    create a hash as stamp to verbs
    """
    
    if not verb:
        return ""
    verb = ar.strip_tashkeel(verb)
    if not verb:
        return ""
    
    # if verb is  4 length
    # remove all
    if len(verb)==4 and verb.startswith(ar.ALEF_HAMZA_ABOVE):
        verb = verb.replace(ar.ALEF_HAMZA_ABOVE, '')
    # normalize hamza
    # Alef Madd will be converted into hamza + alef, the alef will be removed
    verb = re.sub(u'[أإءؤئآ]', u"ء", verb)
    # remove weak letters
    verb = re.sub(u'[اويى]', u"", verb)
    # remove double letter at the end
    if verb[-1:] == verb[-2:-1]:
        verb = verb[:-1]
    
    return verb
    
